import React, { useState } from 'react';
import { TextField, Button, Typography, Paper } from '@material-ui/core'
import useStyles from './styles';

const Form = () => {

  const [movieData, setMovieData] = useState({filmmaker:'', title:'', description:'', selectedFile: ''})
  const classes = useStyles();
  const handleSubmit = () => {

  }

  return (
    <Paper className={classes.paper} >
      <form autoComplete='off' noValidate className={classes.form} onSubmit={handleSubmit}>
        <Typography variant="h6">Create a Movie</Typography>
        <TextField
          name="filmmaker"
          variant="outlined"
          label="Filmmaker"
          fullWidth
          value={movieData.filmmaker}
          onChange={(e) => setMovieData({ ...movieData, filmmaker: e.target.value })}
        />
        <TextField
          name="description"
          variant="outlined"
          label="Description"
          fullWidth
          value={movieData.description}
          onChange={(e) => setMovieData({ ...movieData, description: e.target.value })}
        />
        <TextField
          name="filmmaker"
          variant="outlined"
          label="Filmmaker"
          fullWidth
          value={movieData.creator}
          onChange={(e) => setMovieData({ ...movieData, filmmaker: e.target.value })}
        />
      </form>
    </Paper>
  );
}

export default Form;
